import React from 'react'
import './style.css'

//methods
import {setUser} from '../../config/firebase'

//localStorage
const userId = localStorage.getItem('userId')

export default class Profile extends React.Component{
    constructor(props) {
        super(props)
        this.state = {
            flag: false,
            pictureArr: [],
            boolean: false,
            userName: '',
            usreNumber: '',
            beverages: [],
            timeDuration : [],
            state : 'basic'
        }
    }
    
    stateHandling = (state , value)=>{
        this.setState({
            state : value
        })
    }
    
    forBasic = async()=>{
        const {state, userName, usreNumber} = this.state
        if(!userName || !usreNumber){
            alert("All fields required!")
            return
        }
        try {
            await setUser(userId , {userName, usreNumber})
            this.setState({state : 'beverages'})
        } catch (error) {
            console.log('basicError==>' ,error)
        }
    }

    // forImages = async()=>{
    //     const {state , pictureArr} = this.state
    // } 

    beverageCheckBox = (value, isSelected)=>{
        let {state , beverages} = this.state
        if(isSelected){
            beverages.push(value)
            this.setState({beverages})
        }
    }

    // timeDurationCheckBox = ()



    render() {
        const { state , pictureArr, userName, usreNumber, beverages, timeduration } = this.state
        return (
            <div className='mainContainer'>
               {state == 'basic' && (
                    <div className="neatedWrapper">
                        <h1>Welcome</h1>
                        <div className='inputWrapper'>
                            <input type='text' value={userName} placeholder='Enter your nick Name' onChange={e=>this.setState({userName : e.currentTarget.value})}/>
                            <input type='number' value={usreNumber} placeholder='Enter your phone number' onChange={e => this.setState({usreNumber : e.currentTarget.value})}/>
                        </div>
                        <button className='nextButton'>Next</button>
                    </div>
               )}
                {state == 'beverages' && (
                    <div className="optionsWrapper">
                    <div className='beverageWrapper'>
                        <h1>What do you want on meeting?</h1>
                        <div className='nestedchild'>
                            <div className='checkBoxWrapper'>
                                <input type='checkbox' value ='cocktail' onClick={e=>{this.forBeverages(e.currentTarget.checked, e.currentTarget.value)}} checked/><label>Cocktail</label>
                            </div>
                            <div className='checkBoxWrapper'>
                                <input type='checkbox' value ='juice' onClick={e=>{this.forBeverages(e.currentTarget.checked, e.currentTarget.value)}}/><label>Juice</label>
                            </div>
                            <div className='checkBoxWrapper'>
                                <input type='checkbox' value ='coffee' onClick={e=>{this.forBeverages(e.currentTarget.checked, e.currentTarget.value)}} /><label>Coffee</label>
                            </div>
                        </div>
                        <div className='buttonWrapper'>
                        <button className='nextBtn' onClick={this.forBeverages}>NEXT</button>
                        </div> 
                    </div>                   
                </div>
                )}
                {state === 'meetingTime' && (
                    <div className="optionsWrapper">
                    <div className='beverageWrapper'>
                        <h1>What do you want on meeting?</h1>
                        <div className='nestedchild'>
                            <div className='checkBoxWrapper'>
                                <input type='checkbox' value ='30 mints' onClick={e=>{this.forBeverages(e.currentTarget.checked, e.currentTarget.value)}} checked/><label>30 mintues</label>
                            </div>
                            <div className='checkBoxWrapper'>
                                <input type='checkbox' value ='60 mints' onClick={e=>{this.forBeverages(e.currentTarget.checked, e.currentTarget.value)}} /><label>90 mintues</label>
                            </div>
                            <div className='checkBoxWrapper'>
                                <input type='checkbox' value ='120 mints' onClick={e=>{this.forBeverages(e.currentTarget.checked, e.currentTarget.value)}} /><label>120 mintues</label>
                            </div>
                        </div>
                        <div className='buttonWrapper'>
                        <button className='nextBtn'>NEXT</button>
                        </div> 
                    </div>                   
                </div>
                )}
            </div>
        )
    }
}
